import React, { FunctionComponent, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap';
import Message from '../components/Message';
import { addToCart } from '../actions/cartActions';
interface Props {
  match: any;
  location: any;
  history: any;
}

const CartScreen: FunctionComponent<Props> = ({ match, location, history }) => {
  const productId = match.params.id;

  const qty = location?.search.split('=')[1];

  const dispatch = useDispatch();

  const { cartItems } = useSelector((state: any) => state.cart);
  console.log({ cartItems }, cartItems.lenght === 0, cartItems.lenght);
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty || 1));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = (_id: string) => {
    console.log('rempve');
  };

  console.log(qty);

  return (
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>
        {!cartItems.length ? (
          <Message>
            Your cart is empty <Link to="/">Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item: any) => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>${item.price}</Col>
                  <Col md={2}>
                    <Form.Control
                      as="select"
                      value={item.qty}
                      onChange={(e: React.ChangeEvent<any>) => {
                        dispatch(addToCart(item.product, Number(e.target.value)));
                      }}
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                  </Col>
                  <Col md={2}>
                    <Button type="button" variant="light" onClick={() => removeFromCartHandler(item.product)}>
                      <i className="fas fa-trash"></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
        <Card>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Subtotal ({cartItems.reduce((acc: any, item: any) => acc + item.qty, 0)}) items</h2>$
              {cartItems.reduce((acc: any, item: any) => acc + item.qty * item.price, 0)}
            </ListGroup.Item>
          </ListGroup>
        </Card>
      </Col>
    </Row>
  );
};

export default CartScreen;
